import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

const Payment: React.FC = () => {
  const location = useLocation();
  const { tariffName, tariffPrice } = location.state || { tariffName: '', tariffPrice: 0 };
  const [cardNumber, setCardNumber] = useState('');
  const [expiryDate, setExpiryDate] = useState('');
  const [cvv, setCvv] = useState('');
  const [error, setError] = useState('');
  const [paymentMethod, setPaymentMethod] = useState('creditCard'); // По умолчанию выбран способ оплаты "Кредитная карта"
  const navigate = useNavigate();

  const handlePayment = async () => {
    if (!cardNumber || !expiryDate || !cvv) {
      setError('Пожалуйста, заполните все поля.');
      return;
    }

    const isCardValid = validateCard(cardNumber);
    if (!isCardValid) {
      setError('Некорректный номер карты.');
      return;
    }

    // Генерация случайного userId от 1 до 5
    const randomUserId = Math.floor(Math.random() * 5) + 1;

    try {
      // Получаем VPN-ключ после успешной оплаты
      const response = await fetch(`http://localhost:5000/purchase-key`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ userId: randomUserId }), // Используем случайный userId
      });
      const data = await response.json();

      if (response.ok) {
      // Сохраняем данные о подписке в localStorage
      const subscriptionData = {
        subscriptionStatus: 'Активная',
        subscriptionPlan: tariffName,
        subscriptionEndDate: '2024-12-31', // Установите реальную дату окончания
      };
      localStorage.setItem('userData', JSON.stringify({
        ...userData, // Добавьте остальную информацию о пользователе
        ...subscriptionData
      }));

      // Перенаправляем на страницу подтверждения, передавая VPN-ключ
      navigate('/confirmation', { state: { tariffName, tariffPrice, vpnKey: data.vpnKey } });
    } else {
      setError(data.message || 'Не удалось получить VPN-ключ.');
    }
  } catch (err) {
    console.error('Ошибка при получении ключа:', err);
    setError('Ошибка сервера. Пожалуйста, попробуйте позже.');
  }
};

  const validateCard = (number: string): boolean => {
    return /^\d{16}$/.test(number); // Простая проверка на 16 цифр
  };

  return (
    <div className="flex flex-col items-center justify-center h-screen bg-gray-100 p-4">
      <h1 className="text-5xl font-bold mb-4">Оплата тарифа: {tariffName}</h1>
      <p className="text-xl mb-4">Цена: {tariffPrice} ₽ в месяц</p>

      {error && <p className="text-red-500 mb-4">{error}</p>}

      {/* Выбор способа оплаты */}
      <div className="mb-4">
        <h2 className="text-2xl font-semibold mb-2">Способы оплаты:</h2>
        <label className="flex items-center">
          <input
            type="radio"
            value="creditCard"
            checked={paymentMethod === 'creditCard'}
            onChange={(e) => setPaymentMethod(e.target.value)}
            className="mr-2"
          />
          Кредитная карта
        </label>
        <label className="flex items-center">
          <input
            type="radio"
            value="paypal"
            checked={paymentMethod === 'paypal'}
            onChange={(e) => setPaymentMethod(e.target.value)}
            className="mr-2"
          />
          PayPal
        </label>
        <label className="flex items-center">
          <input
            type="radio"
            value="yandexMoney"
            checked={paymentMethod === 'yandexMoney'}
            onChange={(e) => setPaymentMethod(e.target.value)}
            className="mr-2"
          />
          Яндекс.Деньги
        </label>
      </div>

      {paymentMethod === 'creditCard' && (
        <>
          <input
            type="text"
            placeholder="Номер карты"
            value={cardNumber}
            onChange={(e) => setCardNumber(e.target.value)}
            className="p-2 border border-gray-300 rounded mb-4 w-80"
          />
          <input
            type="text"
            placeholder="Срок действия (MM/YY)"
            value={expiryDate}
            onChange={(e) => setExpiryDate(e.target.value)}
            className="p-2 border border-gray-300 rounded mb-4 w-80"
          />
          <input
            type="text"
            placeholder="CVV"
            value={cvv}
            onChange={(e) => setCvv(e.target.value)}
            className="p-2 border border-gray-300 rounded mb-4 w-80"
          />
        </>
      )}

      <button
        onClick={handlePayment}
        className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600 transition-colors"
      >
        Оплатить
      </button>
    </div>
  );
};

export default Payment;
