import React, { useEffect, useState } from 'react';

const Profile: React.FC = () => {
  const [user, setUser] = useState<any>(null);
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({ name: '', email: '' });

  useEffect(() => {
    const storedUserData = localStorage.getItem('userData');

    if (!storedUserData) {
      const initialUserData = {
        name: 'Иван Иванов',
        email: 'ivan@example.com',
        subscriptionStatus: 'Активная',
        subscriptionPlan: '6 месяцев',
        subscriptionEndDate: '2024-12-31',
        balance: 1500, // Начальный баланс
      };

      localStorage.setItem('userData', JSON.stringify(initialUserData));
      setUser(initialUserData);
      setFormData({ name: initialUserData.name, email: initialUserData.email });
    } else {
      const userData = JSON.parse(storedUserData);
      setUser(userData);
      setFormData({ name: userData.name, email: userData.email });
    }
  }, []);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({ ...prevData, [name]: value }));
  };

  const handleSubmit = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();

    const updatedUser = { ...user, ...formData };

    localStorage.setItem('userData', JSON.stringify(updatedUser));

    setUser(updatedUser);
    setIsEditing(false);
  };

  if (!user) {
    return <div>Загрузка...</div>;
  }

  return (
    <div className="min-h-screen bg-gray-100 py-8 px-4">
      <div className="max-w-lg mx-auto bg-white p-6 rounded-lg shadow-lg">

        {/* Блок с балансом */}
        <div className="mb-6 p-4 bg-gray-50 rounded-lg shadow-inner">
          <h2 className="text-xl font-medium text-gray-700 mb-4">Баланс</h2>
          <p className="text-lg"><strong>Ваш баланс:</strong> {user.balance} ₽</p>
        </div>

        {/* Блок с информацией о пользователе */}
        <div className="mb-6 p-4 bg-gray-50 rounded-lg shadow-inner">
          <h2 className="text-xl font-medium text-gray-700 mb-4">Информация о пользователе</h2>
          {isEditing ? (
            <form onSubmit={handleSubmit}>
              <div className="mb-4">
                <label className="block text-sm font-medium mb-2">Имя</label>
                <input
                  type="text"
                  name="name"
                  value={formData.name}
                  onChange={handleChange}
                  className="w-full p-2 border border-gray-300 rounded"
                  required
                />
              </div>
              <div className="mb-4">
                <label className="block text-sm font-medium mb-2">Email</label>
                <input
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  className="w-full p-2 border border-gray-300 rounded"
                  required
                />
              </div>
              <div className="text-center">
                <button
                  type="submit"
                  className="px-6 py-2 bg-blue-600 text-white rounded hover:bg-blue-700 transition-colors"
                >
                  Сохранить изменения
                </button>
                <button
                  type="button"
                  onClick={() => setIsEditing(false)}
                  className="ml-4 px-6 py-2 bg-gray-300 text-gray-800 rounded hover:bg-gray-400 transition-colors"
                >
                  Отмена
                </button>
              </div>
            </form>
          ) : (
            <>
              <p className="text-lg"><strong>Имя:</strong> {user.name}</p>
              <p className="text-lg mt-2"><strong>Email:</strong> {user.email}</p>
              <div className="text-center">
                <button
                  onClick={() => setIsEditing(true)}
                  className="mt-4 px-6 py-2 bg-blue-600 text-white rounded hover:bg-blue-700 transition-colors"
                >
                  Изменить данные
                </button>
              </div>
            </>
          )}
        </div>

        {/* Блок со статусом подписки */}
        <div className="mb-6 p-4 bg-gray-50 rounded-lg shadow-inner">
          <h2 className="text-xl font-medium text-gray-700 mb-4">Статус подписки</h2>
          <p className="text-lg"><strong>Подписка:</strong> {user.subscriptionPlan}</p>
          <p className="text-lg mt-2"><strong>Статус:</strong> {user.subscriptionStatus}</p>
          <p className="text-lg mt-2"><strong>Окончание подписки:</strong> {user.subscriptionEndDate}</p>
        </div>

      </div>
    </div>
  );
};

export default Profile;
