import React, { useState, useEffect } from 'react';

// Определим интерфейс для данных пользователя
interface UserData {
  name: string;
  email: string;
  subscriptionStatus: string;
  subscriptionPlan: string;
  subscriptionEndDate: string;
}

// Устанавливаем начальное значение userData как пустой объект UserData
const initialUserData: UserData = {
  name: '',
  email: '',
  subscriptionStatus: '',
  subscriptionPlan: '',
  subscriptionEndDate: '',
};

const Profile: React.FC = () => {
  const [userData, setUserData] = useState<UserData | null>(null); // Изменено на null по умолчанию
  const [isEditing, setIsEditing] = useState(false);
  const [error, setError] = useState('');

  // Загружаем данные пользователя с сервера
  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/user'); // Замените на ваш API
        if (!response.ok) {
          throw new Error('Не удалось загрузить данные пользователя');
        }
        const data: UserData = await response.json(); // Указываем тип данных
        console.log('Данные пользователя загружены:', data); // Логируем данные
        setUserData(data);
      } catch (err) {
        console.error(err);
        setError('Ошибка при загрузке данных пользователя');
      }
    };

    fetchUserData();
  }, []);

  // Обработка изменения полей формы
  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;

    setUserData(prevData => {
      if (prevData) {
        return {
          ...prevData,
          [name]: value,
        };
      }
      return initialUserData; // Если prevData null, возвращаем начальное значение
    });
  };

  // Функция для переключения между режимом просмотра и редактирования
  const toggleEditMode = () => {
    setIsEditing(!isEditing);
  };

  // Функция для сохранения изменений
  const handleSave = async () => {
    if (!userData) return; // Предотвращаем выполнение, если userData null

    try {
      const response = await fetch('http://localhost:5000/api/user', {
        method: 'PUT', // Предполагается, что это метод обновления данных
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(userData),
      });

      if (!response.ok) {
        throw new Error('Не удалось сохранить данные пользователя');
      }

      alert('Данные успешно сохранены!');
      setIsEditing(false);
    } catch (err) {
      console.error(err);
      setError('Ошибка при сохранении данных пользователя');
    }
  };

  // Если данные еще не загружены
  if (userData === null) {
    console.log('Данные пользователя все еще загружаются...'); // Логируем статус загрузки
    return <div>Загрузка...</div>; // Меняем условие на проверку null
  }

  return (
    <div className="min-h-screen bg-gray-100 py-8 px-4">
      <div className="max-w-lg mx-auto bg-white p-6 rounded-lg shadow-lg">
        <h1 className="text-3xl font-semibold text-center text-gray-800 mb-6">Личный кабинет</h1>

        {/* Информация о пользователе */}
        <div className="mb-6">
          <h2 className="text-xl font-medium text-gray-700 mb-4">Информация о пользователе</h2>
          <div className="bg-gray-50 p-4 rounded-lg shadow-inner">
            {isEditing ? (
              <>
                <label className="block text-gray-700 font-medium mb-2">Имя</label>
                <input
                  type="text"
                  name="name"
                  value={userData.name}
                  onChange={handleChange}
                  className="w-full mb-4 p-2 border border-gray-300 rounded"
                />

                <label className="block text-gray-700 font-medium mb-2">Email</label>
                <input
                  type="email"
                  name="email"
                  value={userData.email}
                  onChange={handleChange}
                  className="w-full mb-4 p-2 border border-gray-300 rounded"
                />
              </>
            ) : (
              <>
                <p className="text-lg"><strong>Имя:</strong> {userData.name}</p>
                <p className="text-lg mt-2"><strong>Email:</strong> {userData.email}</p>
              </>
            )}
          </div>
        </div>

        {/* Статус подписки */}
        <div className="mb-6">
          <h2 className="text-xl font-medium text-gray-700 mb-4">Статус подписки</h2>
          <div className="bg-gray-50 p-4 rounded-lg shadow-inner">
            <p className="text-lg"><strong>Подписка:</strong> {userData.subscriptionPlan}</p>
            <p className="text-lg mt-2"><strong>Статус:</strong> {userData.subscriptionStatus}</p>
            <p className="text-lg mt-2"><strong>Окончание подписки:</strong> {userData.subscriptionEndDate}</p>
          </div>
        </div>

        {/* Кнопки для изменения данных */}
        <div className="text-center">
          {isEditing ? (
            <button
              className="px-6 py-2 bg-green-600 text-white rounded hover:bg-green-700 transition-colors mr-2"
              onClick={handleSave}
            >
              Сохранить
            </button>
          ) : (
            <button
              className="px-6 py-2 bg-blue-600 text-white rounded hover:bg-blue-700 transition-colors"
              onClick={toggleEditMode}
            >
              Изменить данные
            </button>
          )}

          {isEditing && (
            <button
              className="px-6 py-2 bg-gray-600 text-white rounded hover:bg-gray-700 transition-colors ml-2"
              onClick={toggleEditMode}
            >
              Отмена
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

export default Profile;
