import React, { useState } from 'react';

const screens = [
  {
    title: 'Добро пожаловать в наш VPN-сервис!',
    description: 'С помощью нашего сервиса вы можете безопасно и анонимно серфить в интернете.',
  },
  {
    title: 'Широкий выбор тарифов',
    description: 'Мы предлагаем различные тарифы, чтобы удовлетворить ваши потребности.',
  },
  {
    title: 'Получите свой ключ',
    description: 'После оплаты вы получите свой уникальный ключ для подключения к VPN.',
  },
];

const WelcomeScreens: React.FC = () => {
  const [currentScreen, setCurrentScreen] = useState(0);

  const nextScreen = () => {
    if (currentScreen < screens.length - 1) {
      setCurrentScreen(currentScreen + 1);
    } else {
      window.location.href = '/dashboard';
    }
  };

  return (
    <div className="flex flex-col items-center justify-center h-screen bg-gradient-to-r from-blue-200 to-purple-300 transition duration-500 ease-in-out">
      <div className="text-center p-6 bg-white rounded-lg shadow-lg transform transition-transform duration-300 ease-in-out hover:scale-105">
        <h1 className="text-4xl font-bold mb-4">{screens[currentScreen].title}</h1>
        <p className="text-lg mb-6">{screens[currentScreen].description}</p>
        <button
          onClick={nextScreen}
          className="mt-4 px-6 py-2 bg-blue-600 text-white rounded hover:bg-blue-700 transition duration-200"
        >
          {currentScreen < screens.length - 1 ? 'Далее' : 'В Личный кабинет'}
        </button>
      </div>
    </div>
  );
};

export default WelcomeScreens;
