import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Login: React.FC = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  // Статические данные для проверки входа
  const userData = {
    name: 'Иван Иванов',
    email: 'ivan@example.com',
    subscriptionStatus: 'Активная',
    subscriptionPlan: '6 месяцев',
    subscriptionEndDate: '2024-12-31',
  };

  const handleSubmit = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();

    // Здесь можно добавить проверку логина и пароля
    if (email === userData.email && password === 'password') { // Замените 'password' на правильный пароль
      localStorage.setItem('userData', JSON.stringify(userData));
      navigate('/profile'); // Перенаправление на страницу профиля после успешного входа
    } else {
      alert('Неверный email или пароль.'); // Уведомление об ошибке
    }
  };

  return (
    <div className="flex items-center justify-center min-h-screen">
      <div className="bg-white p-8 rounded-lg shadow-md w-full max-w-xs">
        <h1 className="text-3xl font-semibold text-center mb-6">Вход</h1>
        <form onSubmit={handleSubmit}>
          <div className="mb-4">
            <label className="block text-sm font-medium mb-2">Email</label>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="w-full p-2 border border-gray-300 rounded"
              placeholder="Введите ваш email"
              required
            />
          </div>
          <div className="mb-4">
            <label className="block text-sm font-medium mb-2">Пароль</label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="w-full p-2 border border-gray-300 rounded"
              placeholder="Введите ваш пароль"
              required
            />
          </div>
          <button
            type="submit"
            className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700 transition-colors"
          >
            Войти
          </button>
        </form>
      </div>
    </div>
  );
};

export default Login;
