import React from 'react';
import { useNavigate } from 'react-router-dom';

const Dashboard: React.FC = () => {
  const navigate = useNavigate();

  const handleTariffSelection = (tariffName: string, tariffPrice: number) => {
    navigate('/payment', { state: { tariffName, tariffPrice } });
  };

  const monthlyPrice1Month = 390; // Цена за 1 месяц
  const monthlyPrice6Months = 195; // Цена за 6 месяцев
  const monthlyPrice1Year = 150; // Цена за 1 год

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gradient-to-b from-blue-500 to-blue-300 py-8 px-4">
      <h1 className="text-4xl font-bold text-white mb-6 text-center">Выберите тариф</h1>
      <p className="text-lg text-white mb-4 text-center">Для продолжения выберите один из тарифов ниже:</p>
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
        {/* Тариф на 1 месяц */}
        <div className="bg-white p-6 rounded-lg shadow-lg transition-transform transform hover:scale-105 hover:shadow-2xl border border-blue-300">
          <h2 className="text-2xl sm:text-3xl font-semibold text-center mb-2">1 месяц</h2>
          <p className="text-lg sm:text-xl text-center text-gray-600 mb-2">Цена: <span className="font-bold text-blue-500">{monthlyPrice1Month} ₽ / в месяц</span></p>
          <p className="text-xl sm:text-2xl font-bold text-center text-gray-800 bg-gray-100 rounded p-2 mt-4">
            Итого: {monthlyPrice1Month} ₽
          </p>
          <button
            onClick={() => handleTariffSelection('1 месяц', monthlyPrice1Month)}
            className="mt-4 w-full px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700 transition-colors"
          >
            Выбрать
          </button>
        </div>
        {/* Тариф на 6 месяцев */}
        <div className="bg-white p-6 rounded-lg shadow-lg transition-transform transform hover:scale-105 hover:shadow-2xl border border-blue-300">
          <h2 className="text-2xl sm:text-3xl font-semibold text-center mb-2">6 месяцев</h2>
          <p className="text-lg sm:text-xl text-center text-gray-600 mb-1">Цена: <span className="font-bold text-blue-500">{monthlyPrice6Months} ₽ / в месяц</span></p>
          <p className="text-xl sm:text-2xl font-bold text-center text-gray-800 bg-gray-100 rounded p-2 mt-4">
            Итого: {monthlyPrice6Months * 6} ₽
          </p>
          <button
            onClick={() => handleTariffSelection('6 месяцев', monthlyPrice6Months * 6)}
            className="mt-4 w-full px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700 transition-colors"
          >
            Выбрать
          </button>
        </div>
        {/* Тариф на 1 год */}
        <div className="bg-white p-6 rounded-lg shadow-lg transition-transform transform hover:scale-105 hover:shadow-2xl border border-blue-300">
          <h2 className="text-2xl sm:text-3xl font-semibold text-center mb-2">1 год</h2>
          <p className="text-lg sm:text-xl text-center text-gray-600 mb-1">Цена: <span className="font-bold text-blue-500">{monthlyPrice1Year} ₽ / в месяц</span></p>
          <p className="text-xl sm:text-2xl font-bold text-center text-gray-800 bg-gray-100 rounded p-2 mt-4">
            Итого: {monthlyPrice1Year * 12} ₽
          </p>
          <button
            onClick={() => handleTariffSelection('1 год', monthlyPrice1Year * 12)}
            className="mt-4 w-full px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700 transition-colors"
          >
            Выбрать
          </button>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
