import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Profile: React.FC = () => {
  const [user, setUser] = useState<any>(null);
  const [isEditing, setIsEditing] = useState(false);
  const [editedUser, setEditedUser] = useState<any>(null);
  const [error, setError] = useState<string | null>(null);
  const [success, setSuccess] = useState<string | null>(null);
  const navigate = useNavigate();

  useEffect(() => {
    const storedUserData = localStorage.getItem('userData');
    if (storedUserData) {
      const parsedData = JSON.parse(storedUserData);
      setUser(parsedData);
      setEditedUser(parsedData);
    } else {
      navigate('/login');
    }
  }, [navigate]);

  if (!user) {
    return <div>Загрузка...</div>;
  }

  const toggleEdit = () => {
    setIsEditing(!isEditing);
    setError(null);
    setSuccess(null);
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setEditedUser({ ...editedUser, [e.target.name]: e.target.value });
  };

  const handleSave = () => {
    if (!editedUser.name || !editedUser.email) {
      setError('Все поля обязательны для заполнения.');
      return;
    }
    if (!/\S+@\S+\.\S+/.test(editedUser.email)) {
      setError('Введите корректный email.');
      return;
    }

    localStorage.setItem('userData', JSON.stringify(editedUser));
    setUser(editedUser);
    setSuccess('Данные успешно сохранены!');
    toggleEdit();
  };

  return (
    <div className="min-h-screen bg-gray-100 py-4 px-2">
      <div className="max-w-md mx-auto bg-white p-4 rounded-lg shadow-lg">
        <h1 className="text-2xl font-semibold text-center text-gray-800 mb-4">Личный кабинет</h1>

        {error && <div className="text-red-600 mb-2">{error}</div>}
        {success && <div className="text-green-600 mb-2">{success}</div>}

        <div className="mb-4">
          <h2 className="text-lg font-medium text-gray-700 mb-2">Информация о пользователе</h2>
          <div className="bg-gray-50 p-2 rounded-lg shadow-inner">
            <p className="text-sm"><strong>Имя:</strong> {user.name}</p>
            <p className="text-sm mt-1"><strong>Email:</strong> {user.email}</p>
          </div>
        </div>

        <div className="mb-4">
          <h2 className="text-lg font-medium text-gray-700 mb-2">Статус подписки</h2>
          <div className="bg-gray-50 p-2 rounded-lg shadow-inner">
            <p className="text-sm"><strong>Подписка:</strong> {user.subscriptionPlan}</p>
            <p className="text-sm mt-1"><strong>Статус:</strong> {user.subscriptionStatus}</p>
            <p className="text-sm mt-1"><strong>Окончание подписки:</strong> {user.subscriptionEndDate}</p>
          </div>
        </div>

        <div className="text-center mb-4">
          <button
            onClick={toggleEdit}
            className="w-full px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700 transition-colors"
          >
            {isEditing ? 'Отменить' : 'Изменить данные'}
          </button>
        </div>

        {isEditing && (
          <div className="bg-gray-50 p-2 rounded-lg shadow-inner mb-4">
            <h2 className="text-lg font-medium text-gray-700 mb-2">Редактирование данных</h2>
            <div className="mb-2">
              <label className="block text-gray-700 text-sm">Имя:</label>
              <input
                type="text"
                name="name"
                value={editedUser.name}
                onChange={handleChange}
                className="w-full p-2 border border-gray-300 rounded"
              />
            </div>
            <div className="mb-2">
              <label className="block text-gray-700 text-sm">Email:</label>
              <input
                type="email"
                name="email"
                value={editedUser.email}
                onChange={handleChange}
                className="w-full p-2 border border-gray-300 rounded"
              />
            </div>
            <div className="text-center">
              <button
                onClick={handleSave}
                className="w-full px-4 py-2 bg-green-600 text-white rounded hover:bg-green-700 transition-colors"
              >
                Сохранить
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Profile;
