import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

// Компонент для редактирования профиля
const EditProfileModal: React.FC<{ user: any; onClose: () => void; onSave: (updatedUser: any) => void }> = ({ user, onClose, onSave }) => {
  const [formData, setFormData] = useState({ ...user });

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({ ...prevData, [name]: value }));
  };

  const handleSubmit = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    onSave(formData);
    onClose(); // Закрыть модал после сохранения
  };

  return (
    <div className="fixed inset-0 flex items-center justify-center z-50 bg-black bg-opacity-50">
      <div className="bg-white p-6 rounded-lg shadow-lg w-80">
        <h2 className="text-xl font-semibold mb-4">Редактировать профиль</h2>
        <form onSubmit={handleSubmit}>
          <div className="mb-4">
            <label className="block text-gray-700 mb-2" htmlFor="name">Имя</label>
            <input
              type="text"
              id="name"
              name="name"
              value={formData.name}
              onChange={handleChange}
              className="border border-gray-300 rounded w-full p-2"
              required
            />
          </div>
          <div className="mb-4">
            <label className="block text-gray-700 mb-2" htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              className="border border-gray-300 rounded w-full p-2"
              required
            />
          </div>
          <div className="flex justify-between">
            <button
              type="button"
              onClick={onClose}
              className="px-4 py-2 bg-gray-300 rounded hover:bg-gray-400 transition-colors"
            >
              Отмена
            </button>
            <button
              type="submit"
              className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700 transition-colors"
            >
              Сохранить
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

const Profile: React.FC = () => {
  const [user, setUser] = useState<any>(null); // Замените 'any' на фактический тип, если у вас он есть
  const [isEditing, setIsEditing] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const storedUserData = localStorage.getItem('userData');
    if (storedUserData) {
      setUser(JSON.parse(storedUserData));
    } else {
      navigate('/login');
    }
  }, [navigate]);

  if (!user) {
    return <div className="flex items-center justify-center min-h-screen">Загрузка...</div>;
  }

  const handleEdit = () => {
    setIsEditing(true);
  };

  const handleSave = (updatedUser: any) => {
    setUser(updatedUser);
    localStorage.setItem('userData', JSON.stringify(updatedUser)); // Сохраняем обновленные данные в localStorage
  };

  const handleLogout = () => {
    localStorage.removeItem('userData');
    navigate('/login');
  };

  return (
    <div className="min-h-screen bg-gray-100 py-8 px-4">
      <div className="max-w-sm mx-auto bg-white p-6 rounded-lg shadow-lg">
        <h1 className="text-3xl font-semibold text-center text-gray-800 mb-4">Личный кабинет</h1>

        {/* Информация о пользователе */}
        <div className="mb-4">
          <h2 className="text-xl font-medium text-gray-700 mb-2">Информация о пользователе</h2>
          <div className="bg-gray-50 p-4 rounded-lg shadow-inner">
            <p className="text-lg"><strong>Имя:</strong> {user.name}</p>
            <p className="text-lg mt-2"><strong>Email:</strong> {user.email}</p>
          </div>
        </div>

        {/* Статус подписки */}
        <div className="mb-4">
          <h2 className="text-xl font-medium text-gray-700 mb-2">Статус подписки</h2>
          <div className="bg-gray-50 p-4 rounded-lg shadow-inner">
            <p className="text-lg"><strong>Подписка:</strong> {user.subscriptionPlan}</p>
            <p className="text-lg mt-2"><strong>Статус:</strong> {user.subscriptionStatus}</p>
            <p className="text-lg mt-2"><strong>Окончание подписки:</strong> {user.subscriptionEndDate}</p>
          </div>
        </div>

        {/* Кнопки для редактирования и выхода */}
        <div className="text-center mt-6 space-y-4">
          <button
            onClick={handleEdit}
            className="w-full px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
          >
            Изменить данные
          </button>
          <button
            onClick={handleLogout}
            className="w-full px-4 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700 transition-colors"
          >
            Выйти
          </button>
        </div>

        {/* Модальное окно редактирования профиля */}
        {isEditing && (
          <EditProfileModal
            user={user}
            onClose={() => setIsEditing(false)}
            onSave={handleSave}
          />
        )}
      </div>
    </div>
  );
};

export default Profile;
