import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Profile: React.FC = () => {
  const [user, setUser] = useState<any>(null); // Замените 'any' на фактический тип, если у вас он есть
  const navigate = useNavigate();

  useEffect(() => {
    const storedUserData = localStorage.getItem('userData');
    if (storedUserData) {
      setUser(JSON.parse(storedUserData));
    } else {
      // Перенаправляем на страницу входа, если данные пользователя отсутствуют
      navigate('/login');
    }
  }, [navigate]);

  if (!user) {
    return <div>Загрузка...</div>; // Состояние загрузки
  }

  // Функция для редактирования данных пользователя
  const handleEdit = () => {
    alert('Редактирование данных пока не реализовано.');
  };

  // Функция для пополнения баланса
  const handleTopUp = () => {
    alert('Пополнение баланса пока не реализовано.');
  };

  return (
    <div className="min-h-screen bg-gray-100 py-8 px-4">
      <div className="max-w-lg mx-auto space-y-6">
        <h1 className="text-3xl font-semibold text-center text-gray-800 mb-4">Личный кабинет</h1>

        {/* Контейнеры для Баланса и Информации о пользователе на одном уровне */}
        <div className="flex space-x-4">
          {/* Контейнер для Баланса */}
          <div className="bg-blue-100 flex-1 p-6 rounded-lg shadow-lg">
            <h2 className="text-xl font-medium text-gray-700 mb-2">Баланс</h2>
            <p className="text-lg"><strong>{user.balance} ₽</strong></p>

            {/* Кнопка пополнения баланса */}
            <button
              onClick={handleTopUp}
              className="mt-4 px-6 py-2 bg-green-600 text-white rounded hover:bg-green-700 transition-colors"
            >
              Пополнить баланс
            </button>
          </div>

          {/* Контейнер для Информации о пользователе */}
          <div className="bg-blue-100 flex-1 p-6 rounded-lg shadow-lg">
            <h2 className="text-xl font-medium text-gray-700 mb-4">Информация о пользователе</h2>
            <p className="text-lg"><strong>Имя:</strong> {user.name}</p>
            <p className="text-lg mt-2"><strong>Email:</strong> {user.email}</p>
          </div>
        </div>

        {/* Контейнер для Статуса подписки */}
        <div className="bg-blue-100 p-6 rounded-lg shadow-lg">
          <h2 className="text-xl font-medium text-gray-700 mb-4">Статус подписки</h2>
          <p className="text-lg"><strong>Подписка:</strong> {user.subscriptionPlan}</p>
          <p className="text-lg mt-2"><strong>Статус:</strong> {user.subscriptionStatus}</p>
          <p className="text-lg mt-2"><strong>Окончание подписки:</strong> {user.subscriptionEndDate}</p>
        </div>

        {/* Кнопка для изменения данных */}
        <div className="text-center">
          <button
            onClick={handleEdit}
            className="px-6 py-2 bg-blue-600 text-white rounded hover:bg-blue-700 transition-colors"
          >
            Изменить данные
          </button>
        </div>
      </div>
    </div>
  );
};

export default Profile;
