import React, { useState } from 'react';
import { FaLock, FaShieldAlt, FaClipboardCheck } from 'react-icons/fa';

const screens = [
  {
    title: 'Добро пожаловать в наш VPN-сервис!',
    description: 'С помощью нашего сервиса вы можете безопасно и анонимно серфить в интернете.',
    icon: <FaShieldAlt className="text-6xl text-blue-600 mb-4" />,
  },
  {
    title: 'Широкий выбор тарифов',
    description: 'Мы предлагаем различные тарифы, чтобы удовлетворить ваши потребности.',
    icon: <FaClipboardCheck className="text-6xl text-blue-600 mb-4" />,
  },
  {
    title: 'Получите свой ключ',
    description: 'После оплаты вы получите свой уникальный ключ для подключения к VPN.',
    icon: <FaLock className="text-6xl text-blue-600 mb-4" />,
  },
];

const WelcomeScreens: React.FC = () => {
  const [currentScreen, setCurrentScreen] = useState(0);

  const nextScreen = () => {
    if (currentScreen < screens.length - 1) {
      setCurrentScreen(currentScreen + 1);
    } else {
      window.location.href = '/dashboard'; // Или используйте navigate из react-router
    }
  };

  return (
    <div className="flex flex-col items-center justify-center h-screen bg-cover bg-center" style={{ backgroundImage: 'url(/path/to/your/background-image.jpg)' }}>
      <div className="bg-white bg-opacity-80 rounded-lg shadow-lg p-6 text-center">
        {screens[currentScreen].icon}
        <h1 className="text-4xl font-bold text-blue-800 mb-2">{screens[currentScreen].title}</h1>
        <p className="text-lg text-gray-700 mb-6">{screens[currentScreen].description}</p>
        <button
          onClick={nextScreen}
          className="mt-4 px-6 py-2 bg-blue-600 text-white rounded transition duration-300 hover:bg-blue-700"
        >
          {currentScreen < screens.length - 1 ? 'Далее' : 'В Личный кабинет'}
        </button>
      </div>
    </div>
  );
};

export default WelcomeScreens;
