import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const TopUpBalance: React.FC = () => {
  const [amount, setAmount] = useState('');
  const [paymentMethod, setPaymentMethod] = useState('card'); // Состояние для выбора способа пополнения
  const navigate = useNavigate();

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setAmount(e.target.value);
  };

  const handlePaymentMethodChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    setPaymentMethod(e.target.value);
  };

  const handleSubmit = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    const storedUserData = localStorage.getItem('userData');

    if (storedUserData) {
      const userData = JSON.parse(storedUserData);
      const newBalance = userData.balance + Number(amount);
      userData.balance = newBalance;

      localStorage.setItem('userData', JSON.stringify(userData)); // Обновляем данные пользователя в localStorage
      alert(`Ваш баланс успешно пополнен на ${amount} ₽ через ${paymentMethod}. Новый баланс: ${newBalance} ₽`);
      navigate('/profile'); // Перенаправляем на страницу профиля
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-gray-50 to-gray-200 py-8 px-4">
      <div className="max-w-lg mx-auto bg-white p-6 rounded-lg shadow-lg">
        <h1 className="text-3xl font-semibold text-center text-gray-800 mb-6">Пополнение баланса</h1>
        <form onSubmit={handleSubmit}>
          <input
            type="number"
            value={amount}
            onChange={handleChange}
            placeholder="Введите сумму"
            className="w-full p-2 border border-gray-300 rounded mb-4"
            required
          />

          <label className="block mb-2 text-gray-700" htmlFor="payment-method">Выберите способ пополнения:</label>
          <select
            id="payment-method"
            value={paymentMethod}
            onChange={handlePaymentMethodChange}
            className="w-full p-2 border border-gray-300 rounded mb-4"
          >
            <option value="card">Банковская карта</option>
            <option value="paypal">PayPal</option>
            <option value="wallet">Электронный кошелек</option>
          </select>

          <button
            type="submit"
            className="w-full px-4 py-2 bg-blue-600 text-white rounded-lg shadow hover:bg-blue-700 transition-colors"
          >
            Пополнить
          </button>
        </form>
      </div>
    </div>
  );
};

export default TopUpBalance;
